#les fonctions suivantes sont utiles dans le cas ou l on traite un fichier seul, qui n a pas pu etre lie a un vol.
def insert_date_seul(df, TriggerTime):
	@pandas_udf(StringType())
	def pandas_insert_date(series: pd.Series) -> pd.Series:
		date=datetime.strptime(TriggerTime, "%d %b %Y %H:%M:%S")
		return pd.Series([(date+timedelta(milliseconds=int(ms)*100)).strftime("%d %m %Y %H:%M:%S.%f") for ms in series])

	df=df.withColumn('date', pandas_insert_date(df['Frame_100_ms_']))

	return df

#creation dune dataframe a parir dun fichier seul
def create_df(path):
	rdd_brut = sc.textFile(path)
	TriggerTime=trigger_time(rdd_brut)
	header=get_header(rdd_brut)
	rdd = rdd_brut.zipWithIndex().filter(lambda x: x[1]>6 ).map(lambda x: x[0]).map(lambda x: x.split(','))

	df=data_frame(rdd, header)
	l,h,L=detect_doublon(df.columns)
	if l!=[]:
		df=suppr_doublon(df,h,L)

	df=insert_date_seul(df, TriggerTime)

	return df  

