def decalage(df):

	@pandas_udf(StringType())
	def pandas_del_na(series: pd.Series) -> pd.Series:
		t=series.size
		series=series.dropna()
		tna=series.size
		return pd.concat([series, pd.Series([None for i in range(t-tna)])])

	df=df.replace(' ', None)
	for c in df.columns:
		df=df.withColumn(c, pandas_del_na(F.col(c)))

	return df

