def isSameFlight(t1,t2):
	try:
		t1 = datetime.strptime(t1,"%Y%m%d%H%M%S")
		t2 = datetime.strptime(t2,"%Y%m%d%H%M%S")
		if t1 > t2:
			delta= t1-t2
		else:
			delta=t2-t1

		if delta<timedelta(seconds=220):
			return True
		else:
			return False
	except:
		return False

