			if isSameFlight(find_aircraft_DATE_from_path(p1)[:-1], find_aircraft_DATE_from_path(p2)[:-1]):
				try:
					if datetime.strptime(find_aircraft_DATE_from_path(p2)[:-1], "%Y%m%d%H%M%S")  < datetime.strptime(find_aircraft_DATE_from_path(vol[0])[:-1], "%Y%m%d%H%M%S"):
						vol.insert(0, p2)
					# replace with elif
					if datetime.strptime(find_aircraft_DATE_from_path(p2)[:-1], "%Y%m%d%H%M%S")  > datetime.strptime(find_aircraft_DATE_from_path(vol[-1])[:-1], "%Y%m%d%H%M%S"):
