	return non_numeric_string

def extract_infos_from_ACMF_raw_csv_header(ACMF_rdd):
	sixLines_header_as_list = ACMF_rdd.zipWithIndex().filter(lambda a:a[1]<6).map(lambda a:a[0])
	ReportName = sixLines_header_as_list.collect()[0].split("ReportName ",1)[1]
	TriggerTime = sixLines_header_as_list.collect()[3].split("TriggerTime ",1)[1]
	ReportTime = sixLines_header_as_list.collect()[4].split("Report written on ",1)[1]
	TailNumber = sixLines_header_as_list.collect()[5].split("Aircraft Tail Number ",1)[1]
	return ReportName, TriggerTime, ReportTime, TailNumber

def convert_ACMF_raw_csv_file_to_df_ignoring_6linesHeader(ACMF_csvFile_path):
	ACMF_rdd = sc.textFile(ACMF_csvFile_path)
	#All the raw csv ACMF files have a 6 lines header with important informations but giving the file an abnormal structure
	rdd_final = ACMF_rdd.zipWithIndex().filter(lambda a:a[1]>5).map(lambda a:a[0].split(","))
	columns_names = rdd_final.collect()[0]
	skipline = rdd_final.first()
	ACMF_df = rdd_final.filter(lambda a:a!=skipline).toDF(columns_names)
	return ACMF_df

def convert_ACMF_raw_csv_file_to_df(ACMF_csvFile_path):
	ACMF_rdd = sc.textFile(ACMF_csvFile_path)
	#All the raw csv ACMF files have a 6 lines header with important informations but giving the file an abnormal structure
	rdd_final = ACMF_rdd.zipWithIndex().filter(lambda a:a[1]>5).map(lambda a:a[0].split(","))
	columns_names = rdd_final.collect()[0]
	skipline = rdd_final.first()
	ACMF_df = rdd_final.filter(lambda a:a!=skipline).toDF(columns_names)
	ReportName, TriggerTime, ReportTime, TailNumber = extract_infos_from_ACMF_raw_csv_header(ACMF_rdd)
